mapper.select:
select * from product where category_id = ( select code_name from code where code_id = "category_id" and code_category = #{vo.category_id}) and color_id = (select code_name from code where code_id="color_id" and code_category = #{vo.color_id}) and size_id = (select code_name from code where code_id="size_id" and code_category = #{vo.size_id}) and price between #{row} and #{high} and product_name like concat('%', #{cri.keyword}, '%') order by
price ASC price DESC price ASC
limit #{cri.pageStart}, #{cri.amount}

select count(*) from product where category_id = ( select code_name from code where code_id = "category_id" and code_category = #{vo.category_id}) and color_id = (select code_name from code where code_id="color_id" and code_category = #{vo.color_id}) and size_id = (select code_name from code where code_id="size_id" and code_category = #{vo.size_id}) and price between #{row} and #{high} order by
price ASC price DESC price ASC
limit #{cri.pageStart}, #{cri.amount}
