mapper.select:
		select count(*) from product p1
		WHERE
		p1.product_id = (
		SELECT
		MIN(p2.product_id)
		FROM
		product p2
		WHERE
		p2.product_name = p1.product_name
		)       AND p1.product_id = (
            SELECT MIN(p2.product_id)
            FROM product p2
            WHERE p2.product_name = p1.product_name
        )
        AND (
            (
                #{vo.color_id} IS NULL OR #{vo.color_id} = '99'
            )
            OR (
                p1.color_id = (
                    SELECT code_name
                    FROM code
                    WHERE code_id = 'color_id'
                    AND code_category = #{vo.category_id}
                )
            )
        )
        AND (
            (
                #{vo.size_id} IS NULL OR #{vo.size_id} = '99'
            )
            OR (
                p1.size_id = (
                    SELECT code_name
                    FROM code
                    WHERE code_id = 'size_id'
                    AND code_category = #{vo.category_id}
                )
            )
        )


SELECT COUNT(*) FROM product p1
WHERE
p1.category_id = (
    SELECT code_name
    FROM code
    WHERE code_id = 'category_id'
    AND code_category = #{vo.category_id}
)
AND p1.product_id = (
    SELECT MIN(p2.product_id)
    FROM product p2
    WHERE p2.product_name = p1.product_name
)
AND (
    (
        #{vo.color_id} IS NULL OR #{vo.color_id} = '99'
    )
    OR (
        p1.color_id = (
            SELECT code_name
            FROM code
            WHERE code_id = 'color_id'
            AND code_category = #{vo.category_id}
        )
    )
)
AND (
    (
        #{vo.size_id} IS NULL OR #{vo.size_id} = '99'
    )
    OR (
        p1.size_id = (
            SELECT code_name
            FROM code
            WHERE code_id = 'size_id'
            AND code_category = #{vo.category_id}
        )
    )
)

mapper.insert:
INSERT INTO wish (member_id, product_id, `count`)
VALUES (
#{mVo},
(SELECT product_id
FROM product
WHERE color_id =
#{aVo.product.color_id}
AND size_id = #{aVo.product.size_id}
AND
product_name = #{aVo.product.product_name}),
#{aVo.count}
)

mapper.update:
update wish set `count` = `count` + #{num}
where
product_id = (SELECT product_id
FROM product
WHERE product_name =
#{pVo.product_name}
and color_id = #{pVo.color_id}
and size_id =
#{pVo.size_id})
and member_id = #{mVo}

